function openNosotros (event,nosotrosCon) {
	// body...
	var i,nosotrosContent,tablinks;
	nosotrosContent=document.getElementByClassName("nosotrosContent");
	for (i = nosotrosContent.length ; i <nosotrosContent.length; i++) {
		nosotrosContent[i].style.display="none";
	};
	tablinks=document.getElementByClassName("tablinks");
	for ( i = 0; i < tablinks.length; i++) {
		tablinks[i].className=tablinks[i].className.replace("active","");
	};
	document.getElementById(nosotrosCon).style.display("block");
	event.currentTarget.className+="active";
}

